      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarNav">
      <ul class="navbar-nav me-auto mb-2 mb-lg-0">
        <li class="nav-item active">
          <a class="nav-link" routerLink="/">Home</a>
        </li>


        <li class="nav-item dropdown">
          <a class="nav-link dropdown-toggle" href="#" id="navbarDropdown" role="button" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
            User Verification
          </a>
          <div class="dropdown-menu" aria-labelledby="navbarDropdown">
            <a class="dropdown-item" routerLink="/new-application">New Application</a>
            <a class="dropdown-item" routerLink="/application-request">Pending Application Requests</a>
            </div>
        </li>
      </ul>
      <ul class="navbar-nav navbar-right" style="margin-right: 2vw;">
        <ng-container *ngIf="isLoggedIn()">
          <li class="nav-item dropdown">
            <a class="nav-link " href="#" id="navbarDropdown" role="button" 
              aria-expanded="false">
               Welcome {{ currentUser }}
            </a>

              <li><button class="btn btn-primary" (click)="logout()" href="#">Logout</button></li>


        </ng-container>
        <ng-container *ngIf="!isLoggedIn()">
        <li class="nav-item">
          <button class="btn btn-primary" routerLink="/login">Login</button>
        </li>
      </ng-container>
      </ul>
    </div>
  </nav>
