import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ApiService } from '../UserVerification/api.service';
import { bootstrapApplication } from '@angular/platform-browser';
import { TokenService } from '../app.tokenservice';

@Component({
  selector: 'app-nav',
  templateUrl: './nav.component.html',
  styleUrls: ['./nav.component.scss']
})
export class NavComponent implements OnInit{
  loginForm: FormGroup;
  errorMessage: string;
  currentUser: string = '';
  isLoading=false;
  currentuserrole:string;

  constructor(private formBuilder: FormBuilder, private loginService: ApiService,private tokenservice: TokenService) { }

  ngOnInit() {
    this.loginService.unAuthorisedError.subscribe(()=>{
      this.currentUser=this.tokenservice.getToken();
      console.log(this.currentUser);

    })
    this.loginForm = this.formBuilder.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]]
    });
  }

  isLoggedIn(): boolean {
    return this.tokenservice.getToken() != null;
  }
  logout(): void {
    this.tokenservice.setToken(null);
    this.currentUser = '';
  }
