import { HttpClient, HttpErrorResponse } from "@angular/common/http";
import { EventEmitter, Injectable } from "@angular/core";
import { TokenService } from "../app.tokenservice";
import { Observable, throwError } from "rxjs";
import { catchError,map } from 'rxjs/operators';
import { FareDTO } from "./Models/model.faredto";


@Injectable({
    providedIn: 'root'
  })
  export class ApiService {
    private apiUrl = 'https://ridesharingapi.azurewebsites.net/api'; 
    public unAuthorisedError = new EventEmitter<void>();
    private token=this.tokenservice.getToken();
  
    constructor(private faredto:FareDTO,private http: HttpClient , private tokenservice: TokenService) {}

    getdistances(): Observable<any> {
        return this.http.get<any>(`${this.apiUrl}/distances`)
          .pipe(
            catchError(this.handleError)
          );
      }

      getregno(id:string):Observable<any> {
        return this.http.get<any>(`${this.apiUrl}/GetRegNo/${id}`)
        .pipe(catchError(this.handleError));
      }
      getfare(dto:FareDTO):Observable<any> {
        return this.http.get<any>(`${this.apiUrl}/rides/calculatefare?distanceID=${dto.distanceID}&vehicleRegistrationNo=${dto.vehicleRegistrationNo}`)
        .pipe(catchError(this.handleError));
      }

      private handleError(error: HttpErrorResponse) {
        let errorMessage = 'An error occurred';
        if (error.error instanceof ErrorEvent) {
          // Client-side errors
          errorMessage = `Error: ${error.error.message}`;
        } else {
          // Server-side errors
    
          console.log(error);
          errorMessage = `Error Code: ${error.status}\nMessage: ${error}`;
        }
        if(error.status===401){
          this.unAuthorisedError.emit();
        }
        console.error(errorMessage);
        return throwError(error);
      }
  }
