import { Component, ElementRef, OnInit } from '@angular/core';
import { FormBuilder, FormGroup,FormControl, Validators, NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { ApiService } from './api.service';
import { ReactiveFormsModule } from '@angular/forms';
import { HttpClient, HttpErrorResponse, HttpHeaders } from '@angular/common/http';

@Component({
  selector: 'app-newapplication',
  templateUrl: './newapplication.component.html',
  styleUrls: ['./newapplication.component.scss']
})
export class NewapplicationComponent implements OnInit {

  // applicationForm!: FormGroup ;
  companyList: any[]=[];

  errorMessage: string="";

  newapplication = {
    username: "",
    password: "",
    officialEmail: "",
    phoneNumber: new FormControl('',[Validators.required, Validators.minLength(12), Validators.maxLength(12)]),
    designation: "",
    roleId: "",
    employeeeId: "",
    aadharNumber: "",
    applicationStatus: "New",
    companyId: 0,
    licenseNo: null,
    expirationDate: new Date(),
    rta: null,
    alowedVehicles: null
  };

  constructor(
    private formBuilder: FormBuilder,
    private dataService: ApiService,
    private router: Router,
    private http: HttpClient
  ) { }

  validatePhoneNumber(control: FormControl) {
    const phoneNumber = control.value;
    if (phoneNumber && phoneNumber.length !== 10) {
      return { phoneNumberInvalid: true };
    }
    return null;
  }

  ngOnInit() {


    // Fetch the list of companies from the API
    this.dataService.getCompanies().subscribe((response: any[]) => {


      this.companyList = response;

    });
  }

  // Handler for form submission
  onSubmit(form: NgForm) {
    // Check if the form is valid


    // // Send the application data to the API
    // this.dataService.createApplication(this.applicationForm.value).subscribe((response: any) => {
    //   // Redirect to acknowledgement page
    //   this.router.navigateByUrl('/acknowledgement');
    //});
    console.log(this.newapplication);
    this.dataService.createApplication(this.newapplication).subscribe(response => {
      console.log(response);

  },error=>{
      console.log(error);
  });

}
}
