import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { ApiService } from './api.service';
import { ReactiveFormsModule } from '@angular/forms';

@Component({
  selector: 'app-newapplication',
  templateUrl: './newapplication.component.html',
  styleUrls: ['./newapplication.component.scss']
})
export class NewapplicationComponent implements OnInit {

  applicationForm!: FormGroup ;
  companyList: any[]=[];
  roleList: any[] = [
    { name: 'Motorist', value: 'MOTORIST' },
    { name: 'Rider', value: 'RIDER' }
  ];

  constructor(
    private formBuilder: FormBuilder,
    private dataService: ApiService,
    private router: Router
  ) { }

  ngOnInit() {
    // Initialize the form with required fields and validators
    this.applicationForm = this.formBuilder.group({
      firstName: ['', Validators.required],
      lastName: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      phoneNumber: ['', Validators.required],
      role: ['', Validators.required],
      licenseNumber: [''],
      company: ['', Validators.required]
    });

    // Fetch the list of companies from the API
    this.dataService.getCompanies().subscribe((response: any[]) => {
      this.companyList = response;
    });
  }

  // Handler for form submission
  onSubmit() {
    // Check if the form is valid
    if (this.applicationForm.invalid) {
      return;
    }

    // Send the application data to the API
    this.dataService.createApplication(this.applicationForm.value).subscribe((response: any) => {
      // Redirect to acknowledgement page
      this.router.navigateByUrl('/acknowledgement');
    });
  }

}
<h2>New Application</h2>
<!-- <div *ngIf="errorMessage" class="alert alert-danger">{{ errorMessage }}</div> -->
<form [formGroup]="applicationForm" (ngSubmit)="onSubmit()">
  <div class="form-group">
    <label for="name">Name:</label>
    <input type="text" class="form-control" id="name" formControlName="name">
    <!-- <div *ngIf="applicationForm.get('name').invalid && (applicationForm.get('name').dirty || applicationForm.get('name').touched)"
         class="alert alert-danger">
      <div *ngIf="applicationForm.get('name').errors.required">
        Name is required.
      </div>
    </div> -->
  </div>
  <div class="form-group">
    <label for="email">Email:</label>
    <input type="email" class="form-control" id="email" formControlName="email">
    <!-- <div *ngIf="applicationForm.get('email').invalid && (applicationForm.get('email').dirty || applicationForm.get('email').touched)"
         class="alert alert-danger">
      <div *ngIf="applicationForm.get('email').errors.required">
        Email is required.
      </div>
      <div *ngIf="applicationForm.get('email').errors.email">
        Please enter a valid email address.
      </div>
    </div> -->
  </div>
  <div class="form-group">
    <label for="phone">Phone:</label>
    <input type="tel" class="form-control" id="phone" formControlName="phone">
    <!-- <div *ngIf="applicationForm.get('phone').invalid && (applicationForm.get('phone').dirty || applicationForm.get('phone').touched)"
         class="alert alert-danger">
      <div *ngIf="applicationForm.get('phone').errors.required">
        Phone is required.
      </div>
      <div *ngIf="applicationForm.get('phone').errors.pattern">
        Please enter a valid phone number.
      </div>
    </div> -->
  </div>
  <div class="form-group">
    <label for="role">Role:</label>
    <div>
      <label><input type="radio" name="role" value="motorist" formControlName="role"> Motorist</label>
      <label><input type="radio" name="role" value="rider" formControlName="role"> Rider</label>
    </div>
  </div>
  <div class="form-group">
    <label for="company">Company:</label>
    <select class="form-control" id="company" formControlName="company">
      <option value="">Select a company</option>
      <option *ngFor="let company of companyList" [value]="company">{{ company }}</option>
    </select>
    <!-- <div *ngIf="applicationForm.get('company').invalid && (applicationForm.get('company').dirty || applicationForm.get('company').touched)"
         class="alert alert-danger">
      <div *ngIf="applicationForm.get('company').errors.required">
        Company is required.
      </div>
    </div> -->
  </div>
  <div class="form-group" *ngIf="applicationForm.get('role').value === 'motorist'">
    <label for="licenseNumber">License Number:</label>
    <input type="text" class="form-control" id="licenseNumber" formControlName="licenseNumber">
</div>
<div class="form-group" *ngIf="applicationForm.get('role').value === 'rider'">
    <label for="vehicleType">Vehicle Type:</label>
    <input type="text" class="form-control" id="vehicleType" formControlName="vehicleType">
    <!-- <div *ngIf="applicationForm.get('vehicleType').invalid && (applicationForm.get('vehicleType').dirty || applicationForm.get('vehicleType').touched)"
         class="alert alert-danger">
      <div *ngIf="applicationForm.get('vehicleType').errors.required">
        Vehicle type is required.
      </div>
    </div> -->
  </div>
  <button type="submit" class="btn btn-primary" [disabled]="applicationForm.invalid">Submit</button>
</form>
