import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ApiService } from '../UserVerification/newapplication/api.service';
import { TokenService } from '../app.tokenservice';

@Component({
  selector: 'app-nav',
  templateUrl: './nav.component.html',
  styleUrls: ['./nav.component.scss']
})
export class NavComponent {
  loginForm: FormGroup;
  errorMessage: string;
  currentUser: string = '';

  constructor(private formBuilder: FormBuilder, private loginService: ApiService,private tokenservice: TokenService) { }

  ngOnInit() {
    this.loginForm = this.formBuilder.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]]
    });
  }

  isLoggedIn(): boolean {
    return this.tokenservice.getToken() !== null;
  }
  logout(): void {
    this.tokenservice.setToken(null);
    this.currentUser = '';
  }
  onSubmit() {
    const email = this.loginForm.controls['email'].value;
    const password = this.loginForm.controls['password'].value;
    this.loginService.login(email, password).subscribe(
      response => {
        // localStorage.setItem('token', response.token);
        // localStorage.setItem('username', response.name);
        // close the modal on successful login
        console.log(response.token);
        this.currentUser=response.name;
        document.getElementById('closeModal').click();
      },
      error => {
        this.errorMessage = error.message;
        console.log(error);
      }
    );
  }


}
<nav class="navbar navbar-expand-lg navbar-dark bg-dark">
    <a class="navbar-brand" href="#">RideSharingPlatform</a>
    <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarNav">
      <ul class="navbar-nav me-auto mb-2 mb-lg-0">
        <li class="nav-item active">
          <a class="nav-link" routerLink="/">Home</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" routerLink="/new-application">New Application</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" routerLink="/application-requests">Application Requests</a>
        </li>
      </ul>
      <ul class="navbar-nav mnavbar-right" style="margin-right: 2vw;">
        <ng-container *ngIf="isLoggedIn()">
          <li class="nav-item dropdown">
            <a class="nav-link " href="#" id="navbarDropdown" role="button" 
              aria-expanded="false">
               Hi {{ currentUser }}...
            </a>

              <li><button class="btn btn-primary" (click)="logout()" href="#">Logout</button></li>


        </ng-container>
        <ng-container *ngIf="!isLoggedIn()">
        <li class="nav-item">
          <button class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#loginModal">Login</button>
        </li>
      </ng-container>
      </ul>
    </div>
  </nav>
  <div class="modal fade" id="loginModal" tabindex="-1" aria-labelledby="loginModalLabel" aria-hidden="true">
    <div class="modal-dialog">
      <div class="modal-content">
        <div class="modal-header">
          <h5 class="modal-title" id="loginModalLabel">Login</h5>
          <button type="button" id="closeModal" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
        </div>
        <div class="modal-body">
          <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">
            <div class="mb-3">
              <label for="emailInput" class="form-label">Email address</label>
              <input type="email" class="form-control" id="emailInput" formControlName="email" placeholder="Enter email" required>
            </div>
            <div class="mb-3">
              <label for="passwordInput" class="form-label">Password</label>
              <input type="password" class="form-control" id="passwordInput" formControlName="password" placeholder="Password"  required>
            </div>
            <div *ngIf="errorMessage" class="text-danger">{{ errorMessage }}</div>
            <button type="submit" class="btn btn-primary">Login</button>

          </form>
        </div>
      </div>
    </div>
  </div>
