import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ApiService } from './api.service';

@Component({
  selector: 'app-new-application',
  templateUrl: './newapplication.component.html',
  styleUrls: ['./newapplication.component.css']
})
export class NewapplicationComponent implements OnInit {

  applicationForm!: FormGroup;

  constructor(private formBuilder: FormBuilder, private dataService: ApiService) { }

  ngOnInit(): void {
    this.applicationForm = this.formBuilder.group({
      firstName: ['', Validators.required],
      lastName: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      role: ['', Validators.required],
      company: ['', Validators.required],
      licenseNumber: ['', Validators.required]
    });
  }

  onSubmit(): void {
    if (this.applicationForm.invalid) {
      return;
    }
    this.dataService.createApplication(this.applicationForm.value).subscribe(response => {
      console.log(response);
      // show acknowledgement message here
    }, error => {
      console.log(error);
      // show error message here
    });
  }

}
<h1>New Application</h1>
<form [formGroup]="applicationForm" (ngSubmit)="onSubmit()">
  <div class="form-group">
    <label for="firstName">First Name:</label>
    <input type="text" id="firstName" class="form-control" formControlName="firstName">
    <div *ngIf="applicationForm.get('firstName').invalid && (applicationForm.get('firstName').dirty || applicationForm.get('firstName').touched)" class="invalid-feedback">
      <div *ngIf="applicationForm.get('firstName').errors.required">First Name is required</div>
    </div>
  </div>
  <div class="form-group">
    <label for="lastName">Last Name:</label>
    <input type="text" id="lastName" class="form-control" formControlName="lastName">
    <div *ngIf="applicationForm.get('lastName').invalid && (applicationForm.get('lastName').dirty || applicationForm.get('lastName').touched)" class="invalid-feedback">
      <div *ngIf="applicationForm.get('lastName').errors.required">Last Name is required</div>
    </div>
  </div>
  <div class="form-group">
    <label for="email">Email:</label>
    <input type="email" id="email" class="form-control" formControlName="email">
    <div *ngIf="applicationForm.get('email').invalid && (applicationForm.get('email').dirty || applicationForm.get('email').touched)" class="invalid-feedback">
      <div *ngIf="applicationForm.get('email').errors.required">Email is required</div>
      <div *ngIf="applicationForm.get('email').errors.email">Email is invalid</div>
    </div>
